(1, 'tpl.PageBreaker.ajax', '', 0, 1, 0, '<script type=''text/javascript''>\n  var elem = ''#content''; // ???????, ? ??????? ????? ??????????? ????? ?????? ??? ????? ?? ????/???? ????????\n  \n  $(document).ready(function() {\n    $(''.load_page'').live(''click'', function() {\n      var href = $(this).attr(''href'');\n      $.post(href, {''action'': ''PageBreakLoad''}, function(data) {\n	      //alert(data)\n	      $(elem).html(data);\n      })\n      return false;\n    })\n  })\n\n</script>', 0, 'a:0:{}'),
(2, 'tpl.PageBreaker.navigation', '', 0, 1, 0, '<div style="margin-top: 10px; text-align: center;">\n  [[+pb.link_prev:isnotempty=`<a href="[[+pb.link_prev]]" class="load_page">&larr;&nbsp;????. ???.</a>`]]\n  &nbsp;&nbsp;\n  <b>[[+pb.current]]</b> ?? <b>[[+pb.total]]</b>\n  &nbsp;&nbsp;\n  [[+pb.link_next:ne=``:then=`<a href="[[+pb.link_next]]" class="load_page">????. ???.&nbsp;&rarr;</a>`:else=`<a href="[[~[[*id]]]]" class="load_page">? ??????</a>`]]\n</div>', 0, 'a:0:{}'),
(20, 'blogPost', '', 0, 0, 0, '<div class="post">\n    <h2 class="title"><a href="[[~[[+id]]]]">[[+pagetitle]]</a></h2>\n    <p class="post-info">Posted by [[+createdby:userinfo=`fullname`]]\n [[+tv.tags:notempty=` | <span class="tags">Tags:\n[[!tolinks? &items=`[[+tv.tags]]` &key=`tag` &target=`1`]]\n</span>`]]</p>\n    <div class="entry">\n    <p>[[+introtext]]</p>\n    </div>\n    <p class="postmeta">\n      <span class="links">\n<a href="[[~[[+id]]]]" class="readmore">Read more</a>\n| <a href="[[~[[+id]]]]#comments" class="comments">\n    Comments ([[!QuipCount? &thread=`blog-post-[[+id]]`]])\n  </a>\n| <span class="date">[[+publishedon:strtotime:date=`%b %d, %Y`]]</span>\n      </span>\n    </p>\n</div>', 0, 'a:0:{}'),
(21, 'latestPostsTpl', '', 0, 0, 0, '<li>\n  <a href="[[~[[+id]]]]">[[+pagetitle]]</a>\n  [[+publishedon:notempty=`<br /> - [[+publishedon:strtotime:date=`%b %d, %Y`]]`]]\n</li>', 0, 'a:0:{}'),
