;\n\n/* iterate */\n$pagePlaceholders = array();\n$output = array();\n$alt = false;\n$commentArray = array();\n/** @var quipComment $comment */\nforeach ($comments as $comment) {\n    $commentArray = $comment->toArray();\n    $commentArray[''bodyLimit''] = $bodyLimit;\n    $commentArray[''url''] = $comment->makeUrl(0,array(),array(\n        ''scheme'' => ''full'',\n    ));\n\n    if (!empty($stripTags)) { $commentArray[''body''] = strip_tags($commentArray[''body'']); }\n\n    $commentArray[''ago''] = $quip->getTimeAgo($commentArray[''createdon'']);\n\n    $output[] = $quip->getChunk($tpl,$commentArray);\n    $alt = !$alt;\n}\n\n/* set page placeholders */\n$pagePlaceholders = array();\n$pagePlaceholders[''resource''] = $commentArray[''resource''];\n$pagePlaceholders[''pagetitle''] = empty($pagetitle)? $commentArray[''pagetitle''] : $pagetitle;\n$placeholderPrefix = $modx->getOption(''placeholderPrefix'',$scriptProperties,''quip.latest'');\n$modx->toPlaceholders($pagePlaceholders,$placeholderPrefix);\n\n/* generate output and wrap */\n$outputSeparator = $modx->getOption(''outputSeparator'',$scriptProperties,"\\n");\n$output = implode($outputSeparator,$output);\n\nif (!empty($containerTpl)) {\n    $output = $quip->getChunk($containerTpl,array(\n        ''comments'' => $output,\n    ));\n}\n\n/* set to placeholder or return output */\n$toPlaceholder = $modx->getOption(''toPlaceholder'',$scriptProperties,false);\nif ($toPlaceholder) {\n    $modx->setPlaceholder($toPlaceholder,$output);\n    return '''';\n}\nreturn $output;', 0, 'a:17:{s:4:"type";a:6:{s:4:"name";s:4:"type";s:4:"desc";s:24:"quip.prop_late_type_desc";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:5:"value";s:3:"all";s:4:"text";s:8:"quip.all";}i:1;a:2:{s:5:"value";s:6:"thread";s:4:"text";s:11:"quip.thread";}i:2;a:2:{s:5:"value";s:6:"family";s:4:"text";s:11:"quip.family";}i:3;a:2:{s:5:"value";s:4:"user";s:4:"text";s:9:"quip.user";}}s:5:"value";s:3:"all";s:7:"lexicon";s:15:"quip:properties";}s:3:"tpl";a:6:{s:4:"name";s:3:"tpl";s:4:"desc";s:22:"quip.prop_rss_tpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:14:"quipRssComment";s:7:"lexicon";s:15:"quip:properties";}s:12:"containerTpl";a:6:{s:4:"name";s:12:"containerTpl";s:4:"desc";s:31:"quip.prop_rss_containertpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:16:"quipRssContainer";s:7:"lexicon";s:15:"quip:properties";}s:6:"thread";a:6:{s:4:"name";s:6:"thread";s:4:"desc";s:26:"quip.prop_late_thread_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:24:"quip.prop_late_user_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:6:"family";a:6:{s:4:"name";s:6:"family";s:4:"desc";s:26:"quip.prop_late_family_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:9:"stripTags";a:6:{s:4:"name";s:9:"stripTags";s:4:"desc";s:29:"quip.prop_late_striptags_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";s:15:"quip:properties";}s:10:"dateFormat";a:6:{s:4:"name";s:10:"dateFormat";s:4:"desc";s:30:"quip.prop_late_dateformat_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:21:"%b %d, %Y at %I:%M %p";s:7:"lexicon";s:15:"quip:properties";}s:9:"bodyLimit";a:6:{s:4:"name";s:9:"bodyLimit";s:4:"desc";s:29:"quip.prop_late_bodylimit_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:30;s:7:"lexicon";s:15:"quip:properties";}s:5:"limit";a:6:{s:4:"name";s:5:"limit";s:4:"desc";s:25:"quip.prop_late_limit_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:5;s:7:"lexicon";s:15:"quip:properties";}s:5:"start";a:6:{s:4:"name";s:5:"start";s:4:"desc";s:25:"quip.prop_late_start_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:0;s:7:"lexicon";s:15:"quip:properties";}s:6:"sortBy";a:6:{s:4:"name";s:6:"sortBy";s:4:"desc";s:26:"quip.prop_late_sortby_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:9:"createdon";s:7:"lexicon";s:15:"quip:properties";}s:11:"sortByAlias";a:6:{s:4:"name";s:11:"sortByAlias";s:4:"desc";s:31:"quip.prop_late_sortbyalias_desc";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"text";s:12:"quip.comment";s:5:"value";s:11:"quipComment";}i:1;a:2:{s:4:"text";s:11:"quip.author";s:5:"value";s:6:"Author";}}s:5:"value";s:11:"quipComment";s:7:"lexicon";s:15:"quip:properties";}s:7:"sortDir";a:6:{s:4:"name";s:7:"sortDir";s:4:"desc";s:27:"quip.prop_late_sortdir_desc";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"text";s:14:"quip.ascending";s:5:"value";s:3:"ASC";}i:1;a:2:{s:4:"text";s:15:"quip.descending";s:5:"value";s:4:"DESC";}}s:5:"value";s:4:"DESC";s:7:"lexicon";s:15:"quip:properties";}s:8:"contexts";a:6:{s:4:"name";s:8:"contexts";s:4:"desc";s:28:"quip.prop_late_contexts_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:13:"toPlaceholder";a:6:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:33:"quip.prop_late_toplaceholder_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:17:"placeholderPrefix";a:6:{s:4:"name";s:17:"placeholderPrefix";s:4:"desc";s:36:"quip.prop_rss_placeholderprefix_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:11:"quip.latest";s:7:"lexicon";s:15:"quip:properties";}}', '');
