(22, 'ConfirmRegister', 'Handles activation of registered user.', 0, 7, 0, '/**\n * Register\n *\n * Copyright 2010 by Shaun McCormick <shaun@modx.com>\n *\n * Register is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Register is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Register; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package login\n */\n/**\n * MODx Confirm Register Activation Snippet. Snippet to place on an activation\n * page that the user using the Register snippet would be sent to via the\n * activation email.\n *\n * @package login\n */\n$model_path = $modx->getOption(''core_path'').''components/login/model/login/'';\n$Login = $modx->getService(''login'',''Login'',$model_path,$scriptProperties);\n$modx->lexicon->load(''login:register'');\n\n/* get default properties */\n$errorPage = $modx->getOption(''errorPage'',$scriptProperties,false);\n\n/* get user from query params */\nif (empty($_REQUEST[''lp'']) || empty($_REQUEST[''lu''])) {\n    if (!empty($errorPage)) {\n        $url = $modx->makeUrl($errorPage,'''','''',''full'');\n        $modx->sendRedirect($url);\n    } else {\n        $modx->sendErrorPage();\n    }\n}\n$username = base64_decode(urldecode(rawurldecode($_REQUEST[''lu''])));\n$password = base64_decode(urldecode(rawurldecode($_REQUEST[''lp''])));\n\n/* validate we have correct user */\n$user = $modx->getObject(''modUser'',array(''username'' => $username));\nif ($user == null || $user->get(''active'')) { \n    if (!empty($errorPage)) {\n        $url = $modx->makeUrl($errorPage,'''','''',''full'');\n        $modx->sendRedirect($url);\n    } else {\n        $modx->sendErrorPage();\n    }\n}\n\n/* validate password to prevent middleman attacks */\n$modx->getService(''registry'', ''registry.modRegistry'');\n$modx->registry->addRegister(''login'',''registry.modFileRegister'');\n$modx->registry->login->connect();\n$modx->registry->login->subscribe(''/useractivation/''.$user->get(''username''));\n$msgs = $modx->registry->login->read();\nif (empty($msgs)) $modx->sendErrorPage();\n$found = false;\nforeach ($msgs as $msg) {\n    if ($msg == $password) $found = true;\n}\nif (!$found) {\n    if (!empty($errorPage)) {\n        $url = $modx->makeUrl($errorPage,'''','''',''full'');\n        $modx->sendRedirect($url);\n    } else {\n        $modx->sendErrorPage();\n    }\n}\n\n/* invoke OnBeforeUserActivateEvent, if result returns anything, do not proceed */\n$result = $modx->invokeEvent(''OnBeforeUserActivate'',array(\n    ''user'' => &$user,\n));\n$preventActivation = $Login->getEventResult($result);\nif (!empty($preventActivation)) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''[Register] OnBeforeUserActivate event prevented activation for "''.$user->get(''username'').''" by returning false: ''.$preventActivation);\n    if (!empty($errorPage)) {\n        $url = $modx->makeUrl($errorPage,'''','''',''full'');\n        $modx->sendRedirect($url);\n    } else {\n        $modx->sendErrorPage();\n    }\n}\n\n/* activate user */\n$user->set(''active'',1);\n$user->set(''cachepwd'','''');\nif (!$user->save()) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''[Register] Could not save activated user: ''.$user->get(''username''));\n    return '''';\n}\n\n/* invoke OnUserActivate event */\n$modx->invokeEvent(''OnUserActivate'',array(\n    ''user'' => &$user,\n));\n\n/* authenticate user to contexts */\nif ($modx->getOption(''authenticate'',$scriptProperties,true)) {\n    $modx->user =& $user;\n    $modx->getUser();\n    $contexts = !empty($scriptProperties[''authenticateContexts'']) ? $scriptProperties[''authenticateContexts''] : $modx->context->get(''key'');\n    $contexts = explode('','',$contexts);\n    foreach ($contexts as $ctx) {\n        $modx->user->addSessionContext($ctx);\n    }\n}\n\n/* if wanting to redirect after confirmed registration (for shopping carts)\n * Also allow &redirectBack parameter sent in confirmation email to redirect\n * to a form requiring registration\n */\n$redirectBack = $modx->getOption(''redirectBack'',$_REQUEST,$modx->getOption(''redirectBack'',$scriptProperties,''''));\n$redirectBackParams = $redirectBackParams = $modx->getOption(''redirectBackParams'',$_REQUEST,$modx->getOption(''redirectBackParams'',$scriptProperties,''''));\nif (!empty($redirectBackParams)) {\n    $redirectBackParams = $Login->decodeParams($redirectBackParams);\n}\n$redirectTo = !empty($scriptProperties[''redirectTo'']) ? $scriptProperties[''redirectTo''] : $redirectBack;\nif (!empty($redirectTo)) {\n    /* allow custom redirection params */\n    $redirectParams = !empty($scriptProperties[''redirectParams'']) ? $scriptProperties[''redirectParams''] : $redirectBackParams;\n    if (!empty($redirectParams) && !is_array($redirectParams)) $redirectParams = $modx->fromJSON($redirectParams);\n    if (empty($redirectParams) || !is_array($redirectParams)) $redirectParams = array();\n\n    /* handle persist params from Register snippet */\n    $persistParams = $_GET;\n    unset($persistParams[''lp''],$persistParams[''lu'']);\n    $persistParams[''username''] = $user->get(''username'');\n    $persistParams[''userid''] = $user->get(''id'');    \n    $redirectParams = array_merge($redirectParams,$persistParams);\n    unset($redirectParams[$modx->getOption(''request_param_alias'',null,''q'')],$redirectParams[''redirectBack'']);\n\n    /* redirect user */\n    $url = $modx->makeUrl($redirectTo,'''',$redirectParams);\n    $modx->sendRedirect($url);\n}\n\nreturn '''';', 0, 'a:5:{s:10:"redirectTo";a:6:{s:4:"name";s:10:"redirectTo";s:4:"desc";s:36:"prop_confirmregister.redirectto_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}s:14:"redirectParams";a:6:{s:4:"name";s:14:"redirectParams";s:4:"desc";s:40:"prop_confirmregister.redirectparams_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}s:12:"authenticate";a:6:{s:4:"name";s:12:"authenticate";s:4:"desc";s:38:"prop_confirmregister.authenticate_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";s:16:"login:properties";}s:20:"authenticateContexts";a:6:{s:4:"name";s:20:"authenticateContexts";s:4:"desc";s:46:"prop_confirmregister.authenticatecontexts_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}s:9:"errorPage";a:6:{s:4:"name";s:9:"errorPage";s:4:"desc";s:35:"prop_confirmregister.errorpage_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}}', ''),
(23, 'UpdateProfile', 'Allows front-end updating of a users own profile.', 0, 7, 0, '/**\n * UpdateProfile\n *\n * Copyright 2010 by Shaun McCormick <shaun@modx.com>\n *\n * Register is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Register is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Register; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package login\n */\n/**\n * MODx UpdateProfile Snippet. Handles updating of User Profiles.\n *\n * @package login\n */\nrequire_once $modx->getOption(''login.core_path'',null,$modx->getOption(''core_path'').''components/login/'').''model/login/login.class.php'';\n$login = new Login($modx,$scriptProperties);\n$modx->lexicon->load(''login:updateprofile'');\n$modx->lexicon->load(''login:register'');\n\n/* setup default properties */\n$preHooks = $modx->getOption(''preHooks'',$scriptProperties,'''');\n$submitVar = $modx->getOption(''submitVar'',$scriptProperties,''login-updprof-btn'');\n$redirectToLogin = $modx->getOption(''redirectToLogin'',$scriptProperties,true);\n$reloadOnSuccess = $modx->getOption(''reloadOnSuccess'',$scriptProperties,true);\n$errTpl = $modx->getOption(''errTpl'',$scriptProperties,''<span class="error">[[+error]]</span>'');\n$emailField = $modx->getOption(''email'',$scriptProperties,''email'');\n$validate = $modx->getOption(''validate'',$scriptProperties,'''');\n$placeholderPrefix = $modx->getOption(''placeholderPrefix'',$scriptProperties,'''');\n\n/* verify authenticated status */\nif (!$modx->user->hasSessionContext($modx->context->get(''key''))) {\n    if ($redirectToLogin) {\n        $modx->sendUnauthorizedPage();\n    } else {\n        return '''';\n    }\n}\n\n/* get profile */\n$profile = $modx->user->getOne(''Profile'');\nif (empty($profile)) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''Could not find profile for user: ''.$modx->user->get(''username''));\n    return '''';\n}\n\n$placeholders = $profile->toArray();\n/* add extended fields to placeholders */\nif ($modx->getOption(''useExtended'',$scriptProperties,true)) {\n    $extended = $profile->get(''extended'');\n    if (!empty($extended) && is_array($extended)) {\n        $placeholders = array_merge($extended,$placeholders);\n    }\n}\n$modx->toPlaceholders($placeholders,$placeholderPrefix);\n\n/* if success */\nif (!empty($_REQUEST[''updpsuccess''])) {\n    $modx->setPlaceholder(''login.update_success'',true);\n}\n\nif (!empty($_POST) && (empty($submitVar) || !empty($_POST[$submitVar]))) {\n    /* handle validation */\n    $login->loadValidator();\n    $fields = $login->validator->validateFields($_POST,$validate);\n    foreach ($fields as $k => $v) {\n        $fields[$k] = str_replace(array(''['','']''),array(''&#91;'',''&#93;''),$v);\n    }\n    if (!empty($submitVar)) unset($fields[$submitVar]);\n    \n    /* if allow_multiple_emails setting is false, prevent duplicate emails */\n    if (!empty($fields[$emailField]) && !$modx->getOption(''allow_multiple_emails'',null,false)) {\n        $emailTaken = $modx->getObject(''modUserProfile'',array(\n            ''email'' => $fields[$emailField],\n            ''id:!='' => $modx->user->get(''id''),\n        ));\n        if ($emailTaken) {\n            $login->validator->errors[$emailField] = $modx->lexicon(''login.email_taken'',array(''email'' => $fields[$emailField]));\n        }\n    }\n\n    if (empty($login->validator->errors)) {\n        /* do prehooks */\n        $login->loadHooks(''preHooks'');\n        $login->preHooks->loadMultiple($preHooks,$fields,array(\n            ''submitVar'' => $submitVar,\n            ''redirectToLogin'' => $redirectToLogin,\n            ''reloadOnSuccess'' => $reloadOnSuccess,\n        ));\n        if (!empty($login->preHooks->fields)) {\n            $fields = $login->preHooks->fields;\n        }\n        if (!empty($submitVar)) unset($fields[$submitVar]);\n        \n        /* process hooks */\n        if (!empty($login->preHooks->errors)) {\n            $errors = array();\n            foreach ($login->preHooks->errors as $key => $error) {\n                $errors[$key] = str_replace(''[[+error]]'',$error,$errTpl);\n            }\n            $modx->toPlaceholders($errors,''error'');\n\n            $errorMsg = $login->preHooks->getErrorMessage();\n            $modx->toPlaceholder(''message'',$errorMsg,''error'');\n\n        } else {\n            /* update the profile */\n            $result = require_once $login->config[''processorsPath''].''update.profile.php'';\n            if ($result !== true) {\n                $modx->toPlaceholder(''message'',$result,''error'');\n            } else if ($reloadOnSuccess) {\n                $url = $modx->makeUrl($modx->resource->get(''id''),'''',''?updpsuccess=1'',''full'');\n                $modx->sendRedirect($url);\n            } else {\n                $modx->setPlaceholder(''login.update_success'',true);\n            }\n        }\n    }\n    $modx->toPlaceholders($login->validator->errors,$placeholderPrefix.''error'');\n    $modx->toPlaceholders($fields,$placeholderPrefix);\n}\n\nreturn '''';', 0, 'a:12:{s:9:"submitVar";a:6:{s:4:"name";s:9:"submitVar";s:4:"desc";s:33:"prop_updateprofile.submitvar_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}s:15:"redirectToLogin";a:6:{s:4:"name";s:15:"redirectToLogin";s:4:"desc";s:39:"prop_updateprofile.redirecttologin_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";s:16:"login:pr